mapper.select:
SELECT ad.address_mapping_id, ad.region_id, ad.address_type_code, ad.catalog_id, ad.external_code, ad.external_name, ad.active_flag, ad.OBJECT_VERSION_NUMBER, c.region_code, c.region_name, c.parent_region_id, c.level_path FROM o2md_address_mapping ad left join o2md_region c ON ad.region_id = c.region_id left join o2md_country co on c.country_id = co.country_id WHERE 1=1
AND ad.tenant_id = #{addressMapping.tenantId}
AND ad.catalog_id in (SELECT ca.catalog_id FROM o2md_catalog ca WHERE (ca.catalog_code = #{addressMapping.catalogCode}) AND ca.tenant_id = #{addressMapping.tenantId})
AND ad.external_name LIKE concat("%",concat(#{addressMapping.externalName}),"%")
AND c.region_name LIKE concat("%",concat(#{addressMapping.regionName}),"%")
AND co.country_code = #{countryCode}

SELECT ad.*
FROM o2md_address_mapping ad JOIN o2md_catalog ca ON ca.catalog_id = ad.catalog_id
WHERE 1=1

    AND ca.catalog_code = #{catalogCode}
    AND ca.tenant_id = #{tenantId}


    AND ad.region_id = #{regionId}


    AND ad.tenant_id = #{tenantId}
