mapper.select:



SELECT oft.template_id,
oft.template_code,
oft.template_name,
oft.delivery_free_flag,
oft.valuation_type,
hutt.uom_type_name,
oft.valuation_uom,
hutl.uom_name,
oft.dafault_flag,
oft.object_version_number,
oft.creation_date,
oft.created_by,
oft.last_updated_by,
oft.last_update_date
FROM o2md_freight_template oft
LEFT JOIN hpfm_uom_type hut ON oft.valuation_type=hut.uom_type_code AND hut.enabled_flag = 1 and hut.tenant_id  = #{tenantId}
LEFT JOIN hpfm_uom_type_tl hutt ON hut.uom_type_id=hutt.uom_type_id AND hutt.lang = #{lang}
LEFT JOIN hpfm_uom hu ON oft.valuation_uom=hu.uom_code AND oft.valuation_type = hu.uom_type_code
                            AND hu.enabled_flag = 1 AND hu.tenant_id  = #{tenantId}
LEFT JOIN hpfm_uom_tl hutl ON hu.uom_id=hutl.uom_id AND hutl.lang = #{lang}


        AND oft.template_id LIKE #{templateId}



        AND oft.template_code LIKE #{templateCodeLike}



        AND oft.template_name LIKE #{templateNameLike}


        AND oft.delivery_free_flag = #{deliveryFreeFlag}


        AND oft.valuation_type = #{valuationType}


        AND oft.valuation_uom = #{valuationUom}


        AND oft.tenant_id = #{tenantId}


        AND oft.enabled_flag = #{enabledFlag}



ORDER BY oft.template_code ASC
