mapper.select:

SELECT
os.online_shop_id,
ost.online_shop_name,
os.online_shop_code,
os.platform_shop_code,
os.catalog_code,
os.sourced_flag,
os.picked_up_flag,
os.returned_flag,
os.exchanged_flag,
os.active_flag,
os.enable_split_flag,
os.OBJECT_VERSION_NUMBER,
os.catalog_code,
os.tenant_id,
os.catalog_version_code,
ocvt.catalog_version_name,
oct.catalog_name
FROM
o2md_online_shop os
left join o2md_online_shop_tl ost on os.online_shop_id = ost.online_shop_id and ost.lang = #{lang}
left join o2md_catalog oc on os.catalog_code = oc.catalog_code AND oc.tenant_id = #{tenantId}
left join o2md_catalog_tl oct on oct.catalog_id = oc.catalog_id and oct.lang = #{lang}
left join o2md_catalog_version ocv on ocv.catalog_version_code = os.catalog_version_code AND ocv.tenant_id =
#{tenantId}
left join o2md_catalog_version_tl ocvt on ocvt.catalog_version_id = ocv.catalog_version_id and ocvt.lang =
#{lang}




            AND os.tenant_id = #{tenantId}


            AND 1 = 0




        AND os.online_shop_code LIKE #{onlineShopCodeLike}



        AND os.online_shop_name LIKE #{onlineShopNameLike}


        AND os.active_flag = #{activeFlag}



        AND os.platform_shop_code LIKE #{platformShopCodeLike}


        AND os.catalog_code = #{catalogCode}


        AND os.catalog_version_code = #{catalogVersionCode}


        and os.online_shop_id = #{onlineShopId}


ORDER BY os.online_shop_code

SELECT os.online_shop_id,
os.online_shop_name,
os.online_shop_code,
os.platform_shop_code,
os.catalog_code,
FROM
o2md_online_shop os



            AND os.tenant_id = #{tenantId}


            AND 1 = 0



        AND os.online_shop_code = #{onlineShopCode}


        AND os.online_shop_name = #{onlineShopName}


        AND os.platform_shop_code = #{platformShopCode}


        AND os.catalog_code = #{catalogCode}


        AND os.catalog_version_code = #{catalogVersionCode}


ORDER BY os.online_shop_code


select os.online_shop_id,
os.online_shop_code,
ost.online_shop_name
from o2md_online_shop os
left join o2md_online_shop_tl ost on ost.online_shop_id = os.online_shop_id and ost.lang = #{lang}



            AND os.tenant_id = #{tenantId}


            AND 1 = 0



        AND os.online_shop_code = #{onlineShopCode}


        AND os.online_shop_name = #{onlineShopName}


        AND os.platform_shop_code = #{platformShopCode}


        AND os.catalog_code = #{catalogCode}


        AND os.active_flag = #{activeFlag}


ORDER BY os.online_shop_code
