SELECT ad.address_mapping_id, ad.region_code, ad.address_type_code, ad.platform_code, ad.external_code, ad.external_name, ad.active_flag, ad.OBJECT_VERSION_NUMBER FROM o2md_address_mapping ad WHERE 1=1
AND ad.tenant_id = #{addressMapping.tenantId}
AND 1 =0
AND ad.platform_code = #{addressMapping.platformCode}
AND ad.external_name LIKE concat('%',concat(#{addressMapping.externalName}),'%')

SELECT ad.address_mapping_id, ad.address_type_code, ad.catalog_code, ad.external_code, ad.external_name, ad.active_flag, ad.OBJECT_VERSION_NUMBER, c.region_id, c.region_code, c.region_name, c.parent_region_id FROM hpfm_region c LEFT JOIN o2md_address_mapping ad ON ad.region_id = c.region_id AND ad.tenant_id = #{tenantId} WHERE 1=1
AND c.tenant_id = #{tenantId}
AND 1 = 0
AND c.region_id = #{id} AND ad.catalog_code=#{type}

SELECT ad.address_mapping_id, ad.address_type_code, ad.catalog_code, ad.external_code, ad.external_name, ad.active_flag, ad.OBJECT_VERSION_NUMBER, c.region_id, c.region_code, c.region_name, c.parent_region_id FROM hpfm_region c LEFT JOIN o2md_address_mapping ad ON ad.region_id = c.region_id AND ad.tenant_id = #{tenantId} WHERE 1=1
AND c.tenant_id = #{tenantId}
AND 1 = 0
AND c.region_code = #{regionCode}

SELECT ad.address_mapping_id, ad.region_code, ad.address_type_code, ad.platform_code, ad.external_code, ad.external_name, ad.active_flag, ad.OBJECT_VERSION_NUMBER, ad.tenant_id FROM o2md_address_mapping ad WHERE 1=1
AND ad.tenant_id = #{tenantId}
AND ad.tenant_id = 0
AND ad.region_code = #{regionCode}
AND ad.platform_code = #{platformCode}

SELECT ad.address_mapping_id, ad.address_type_code, ad.platform_code, ad.external_code, ad.external_name, ad.active_flag, ad.region_code, ad.OBJECT_VERSION_NUMBER FROM o2md_address_mapping ad WHERE 1=1
AND ad.tenant_id = #{tenantId}
AND 1 = 0
and ad.region_code IN
#{code}
AND ad.platform_code=#{type}

