mapper.select:
SELECT ad.address_mapping_id, ad.region_code, ad.address_type_code, ad.catalog_code, ad.external_code, ad.external_name, ad.active_flag, ad.OBJECT_VERSION_NUMBER FROM o2md_address_mapping ad WHERE 1=1
AND ad.tenant_id = #{addressMapping.tenantId}
AND 1 =0
AND ad.catalog_code = #{addressMapping.catalogCode}
AND ad.external_name LIKE concat('%',concat(#{addressMapping.externalName}),'%')

SELECT ad.address_mapping_id, ad.address_type_code, ad.catalog_code, ad.external_code, ad.external_name, ad.active_flag, ad.OBJECT_VERSION_NUMBER, c.region_id, c.region_code, c.region_name, c.parent_region_id FROM hpfm_region c LEFT JOIN o2md_address_mapping ad ON ad.region_id = c.region_id AND ad.tenant_id = #{tenantId} WHERE 1=1
AND c.tenant_id = #{tenantId}
AND 1 = 0
AND c.region_id = #{id} AND ad.catalog_code=#{type}

SELECT ad.address_mapping_id, ad.address_type_code, ad.catalog_code, ad.external_code, ad.external_name, ad.active_flag, ad.OBJECT_VERSION_NUMBER, c.region_id, c.region_code, c.region_name, c.parent_region_id FROM hpfm_region c LEFT JOIN o2md_address_mapping ad ON ad.region_id = c.region_id AND ad.tenant_id = #{tenantId} WHERE 1=1
AND c.tenant_id = #{tenantId}
AND 1 = 0
AND c.region_code = #{regionCode}

SELECT ad.address_mapping_id, ad.region_id, ad.address_type_code, ad.catalog_code, ad.external_code, ad.external_name, ad.active_flag, ad.OBJECT_VERSION_NUMBER, ad.tenant_id FROM o2md_address_mapping ad WHERE 1=1
AND ad.tenant_id = #{tenantId}
AND ad.tenant_id = 0
AND ad.region_id = #{regionId}
AND ad.catalog_code = #{catalogCode}

SELECT ad.address_mapping_id, ad.address_type_code, ad.catalog_code, ad.external_code, ad.external_name, ad.active_flag, ad.region_code, ad.OBJECT_VERSION_NUMBER FROM o2md_address_mapping ad WHERE 1=1
AND ad.tenant_id = #{tenantId}
AND 1 = 0
and ad.region_code IN
#{code}
AND ad.catalog_code=#{type}


SELECT
hr.region_code,
hr.parent_region_id,
hr.level_path,
hr.level_number,
hrt.region_name,
hc.country_code,
hc.country_name,
hr.region_id
FROM hpfm_region hr
JOIN hpfm_region_tl hrt ON hr.region_id = hrt.region_id AND hrt.lang = #{lang}
JOIN hpfm_country  hc On hc.country_id = hr.country_id

    hr.enabled_flag = 1


        AND hr.region_name like #{regionName}



        AND hr.region_code like #{regionCode}


        AND hc.country_code = #{countryCode}


        AND hr.parent_region_id = #{parentRegionId}


        AND hr.parent_region_id = (select region_id from hpfm_region where region_code = #{parentRegionCode} and tenant_id = #{tenantId})


        AND hr.enabled_flag = #{enabledFlag}


        and hr.region_code IN

            #{code}




        and hr.region_code not IN

            #{code}




        and hr.region_id  IN

            #{id}



        and hr.region_id  IN

            #{id}




            AND hr.tenant_id = #{tenantId}


            AND 1 = 0
