mapper.select:

SELECT
cm.carrier_mapping_id,
cm.platform_code,
oct.carrier_name,
c.carrier_code,
oct.carrier_name,
cm.platform_carrier_code,
cm.platform_carrier_Name,
cm.object_version_number,
cm.creation_date,
cm.created_by,
cm.last_updated_by,
cm.last_update_date,
ptl.platform_name
FROM
o2md_carrier c
JOIN o2md_carrier_mapping cm ON c.carrier_id = cm.carrier_id AND cm.tenant_id = #{tenantId}
JOIN O2md_platform plat on plat.platform_code = cm.platform_code and plat.tenant_id = #{tenantId}
left join O2md_platform_tl ptl on ptl.platform_id = plat.platform_id and ptl.lang = #{lang}
left join  o2md_carrier_tl oct on oct.carrier_id = c.carrier_id and oct.lang = #{lang}
where 1 = 1


        AND c.tenant_id = #{tenantId}


        AND 1 = 0



    AND cm.platform_code = #{platformCode}



    AND c.carrier_code LIKE #{carrierCode}


    AND plat.platform_type_code = #{platformCode}



    and cm.platform_carrier_code like #{platformCarrierCode}

ORDER BY cm.platform_code, c.carrier_code


SELECT
       c.carrier_code,
       cm.platform_carrier_code,
       tl.carrier_name,
       cm.platform_carrier_name
FROM o2md_carrier_mapping cm
JOIN o2md_carrier c ON c.carrier_id = cm.carrier_id AND cm.tenant_id = #{tenantId}
JOIN o2md_carrier_tl tl on tl.carrier_id=c.carrier_id AND tl.lang =  #{lang}
where 1 = 1


        AND c.tenant_id = #{tenantId}


        AND 1 = 0



    AND cm.platform_code = #{platformCode}
