mapper.select:
SELECT c.catalog_version_code, t.catalog_version_name FROM o2md_catalog_version c left join o2md_catalog_version_tl t on c.catalog_version_id = t.catalog_version_id and t.tenant_id = #{tenantId} and t.lang =#{lang} WHERE 1=1
AND c.tenant_id = #{tenantId}
AND 1 =0
and c.catalog_version_code IN
#{code}


SELECT
cl.catalog_id,
cl.catalog_code,
cltl.catalog_name,
cltl.catalog_remarks,
clv.catalog_version_id,
clv.catalog_version_code,
clvtl.catalog_version_name,
clvtl.catalog_version_remarks
FROM
o2md_catalog_version AS clv
JOIN o2md_catalog AS cl ON clv.catalog_id = cl.catalog_id and cl.tenant_id = #{tenantId}
LEFT JOIN o2md_catalog_version_tl AS clvtl ON clv.catalog_version_id = clvtl.catalog_version_id AND clvtl.lang = #{lang} AND clvtl.tenant_id = #{tenantId}
LEFT JOIN o2md_catalog_tl AS cltl ON cl.catalog_id = cltl.catalog_id AND cltl.lang= #{lang} and cltl.tenant_id = #{tenantId}



            AND clv.tenant_id = #{tenantId}


            AND 1=0



        AND cl.catalog_id = #{catalogId}


        AND clv.active_flag = #{activeFlag}


        AND clv.catalog_version_id = #{catalogVersionId}


        AND cl.catalog_code = #{catalogCode}


        AND cl.catalog_code  <>  'MASTER'



        AND clv.catalog_version_code like #{catalogVersionCode}



        AND clvtl.catalog_version_name like #{catalogVersionName}


SELECT
cl.catalog_code,
cl.active_flag,
cltl.catalog_name,

clv.catalog_version_code as entry_catalog_version_code,
clv.active_flag as entry_active_flag,
clvtl.catalog_version_name as entry_catalog_version_name
FROM
o2md_catalog_version AS clv
JOIN o2md_catalog AS cl ON clv.catalog_id = cl.catalog_id and cl.tenant_id = #{tenantId}
left JOIN o2md_catalog_version_tl AS clvtl ON clv.catalog_version_id = clvtl.catalog_version_id AND clvtl.lang = #{lang} and clvtl.tenant_id = #{tenantId}
left JOIN o2md_catalog_tl AS cltl ON cl.catalog_id = cltl.catalog_id AND cltl.lang= #{lang} and cltl.tenant_id = #{tenantId}



             AND clv.tenant_id = #{tenantId}


             AND 1=0



         and cl.catalog_name IN

             #{name}

select version.catalog_version_id, version.catalog_version_code, version.catalog_version_name, version.catalog_version_remarks, catalog.catalog_id, catalog.catalog_code, version.object_version_number, version.tenant_id, version.active_flag from o2md_catalog_version version inner join o2md_catalog catalog on catalog.catalog_id = version.catalog_id and catalog.tenant_id = #{tenantId}
AND version.tenant_id = #{tenantId}
AND 1=0
and catalog.catalog_code IN
#{code}
