mapper.select:

SELECT
os.online_shop_id,
os.platform_code,
ost.online_shop_name,
os.online_shop_code,
os.platform_shop_code,
os.sourced_flag,
os.picked_up_flag,
os.returned_flag,
os.exchanged_flag,
os.active_flag,
os.enable_split_flag,
os.OBJECT_VERSION_NUMBER,
os.tenant_id,
os.default_currency,
os.is_default,
pftl.platform_name
FROM
o2md_online_shop os
left join o2md_platform pf on pf.platform_code = os.platform_code
left join o2md_platform_tl pftl on pf.platform_id = pftl.platform_id and  pftl.lang = #{lang} and pftl.tenant_id = #{tenantId}
left join o2md_online_shop_tl ost on os.online_shop_id = ost.online_shop_id and ost.lang = #{lang}  and ost.tenant_id = #{tenantId}



            AND os.tenant_id = #{tenantId}
            and pf.tenant_id = #{tenantId}


            AND 1 = 0



        AND os.platform_code = #{platformCode}



        AND os.online_shop_code LIKE (#{onlineShopCodeLike})



        AND os.online_shop_name LIKE #{onlineShopNameLike}


        AND os.active_flag = #{activeFlag}



        AND os.platform_shop_code LIKE #{platformShopCodeLike}


        AND os.catalog_code = #{catalogCode}


        AND os.catalog_version_code = #{catalogVersionCode}


        and os.online_shop_id = #{onlineShopId}



        and os.online_shop_code in

            #{code}




        and

            ost.online_shop_name LIKE  CONCAT('%',#{name},'%')



ORDER BY os.online_shop_code

SELECT os.online_shop_id,
os.platform_code,
os.online_shop_name,
os.online_shop_code,
os.platform_shop_code,
os.catalog_code
FROM
o2md_online_shop os



            AND os.tenant_id = #{tenantId}


            AND 1 = 0



        AND os.online_shop_code = #{onlineShopCode}


        AND os.online_shop_name = #{onlineShopName}


        AND os.platform_shop_code = #{platformShopCode}


        AND os.catalog_code = #{catalogCode}


        AND os.catalog_version_code = #{catalogVersionCode}


ORDER BY os.online_shop_code


select os.online_shop_id,
os.platform_code,
os.online_shop_code,
os.catalog_version_code,
os.catalog_code,
os.platform_shop_code,
ost.online_shop_name,
os.active_flag,
plat.tenant_id,
platl.platform_name
from o2md_online_shop os
inner join  o2md_platform plat on plat.platform_code = os.platform_code and plat.tenant_id = #{tenantId}
left join o2md_platform_tl platl on plat.platform_id = platl.platform_id and platl.lang = #{lang}  and platl.tenant_id = #{tenantId}
left join o2md_online_shop_tl ost on ost.online_shop_id = os.online_shop_id and ost.lang = #{lang} and ost.tenant_id = #{tenantId}



            AND os.tenant_id = #{tenantId}


            AND 1 = 0




        AND os.online_shop_code LIKE upper(#{onlineShopCodeLike})



        AND os.online_shop_name LIKE #{onlineShopName}


        AND os.platform_shop_code = #{platformShopCode}


        AND os.catalog_code = #{catalogCode}


        AND os.platform_code = #{platformCode}


        AND os.active_flag = #{activeFlag}


ORDER BY os.online_shop_code

select tl.online_shop_name, online.online_shop_code, online.catalog_code, oct.catalog_name, online.catalog_version_code, ocvt.catalog_version_name, online.active_flag, online.platform_code, platl.platform_name, online.platform_shop_code, online.picked_up_flag, online.returned_flag, online.exchanged_flag, online.enable_split_flag, online.sourced_flag, online.is_default from o2md_online_shop online inner join o2md_catalog oc on online.catalog_code = oc.catalog_code AND oc.tenant_id = #{tenantId} inner join o2md_catalog_version ocv on ocv.catalog_version_code = online.catalog_version_code AND ocv.tenant_id = #{tenantId} and oc.catalog_id = ocv.catalog_id left join o2md_platform plat on plat.platform_code = online.platform_code and plat.tenant_id = #{tenantId} left join o2md_platform_tl platl on plat.platform_id = platl.platform_id and platl.lang = #{lang} and platl.tenant_id = #{tenantId} left join o2md_online_shop_tl tl on online.online_shop_id = tl.online_shop_id and tl.lang = #{lang} and tl.tenant_id = #{tenantId} left join o2md_catalog_tl oct on oct.catalog_id = oc.catalog_id and oct.lang = #{lang} and oct.tenant_id = #{tenantId} left join o2md_catalog_version_tl ocvt on ocvt.catalog_version_id = ocv.catalog_version_id and ocvt.lang = #{lang} and ocvt.tenant_id = #{tenantId}
AND online.tenant_id = #{tenantId}
AND 1 =0
and online.online_shop_code in
#{code}
and online.platform_shop_code in
#{code}
and online.platform_code = #{onlineShopDTO.platformCode}
and
online.online_shop_name LIKE CONCAT('%',#{name},'%')

select online.online_shop_id, online.platform_code, tl.online_shop_name, online.online_shop_code, online.catalog_code, online.catalog_version_code, online.active_flag from o2md_online_shop online left join o2md_online_shop_tl tl on online.online_shop_id = tl.online_shop_id and tl.tenant_id = #{tenantId} AND tl.lang =#{lang}
AND online.tenant_id = #{tenantId}
AND 1 =0
and online.catalog_code in
#{item.catalogCode}
and online.catalog_version_code in
#{item.catalogVersionCode}
