mapper.select:
SELECT p.pos_id, p.pos_code, p.pos_name, p.pos_status_code, p.pos_type_code, p.business_type_code, p.open_date, p.address_id, p.business_time, p.OBJECT_VERSION_NUMBER, p.CREATION_DATE, p.CREATED_BY, p.LAST_UPDATED_BY, p.LAST_UPDATE_DATE, p.tenant_id FROM o2md_pos p where p.pos_id = #{posId}
AND p.tenant_id = #{tenantId}
AND 1 = 0

SELECT w.warehouse_id, w.pos_id, w.warehouse_code, w.warehouse_name, w.warehouse_type_code from o2md_warehouse w where not exists( select 1 from o2md_online_shop_rel_warehouse srw where srw.warehouse_id = w.warehouse_id and srw.online_shop_id = #{onlineShopId} )
and w.tenant_id = #{tenantId}
and 1 = 0
and w.warehouse_code LIKE #{warehouseCodeLike}
and w.warehouse_name LIKE #{warehouseNameLike}
and w.actived_date_to > sysdate()
and w.actived_date_to > sysdate
order by w.warehouse_code asc

SELECT w.warehouse_id, w.warehouse_code, wtl.warehouse_name, w.warehouse_type_code, w.warehouse_status_code, w.picked_up_flag, w.pick_up_quantity, w.expressed_flag, w.expressed_quantity, w.score, w.actived_date_from, w.actived_date_to, w.pos_id, p.pos_code, ptl.pos_name, w.OBJECT_VERSION_NUMBER, w.CREATION_DATE, w.CREATED_BY, w.LAST_UPDATED_BY, w.LAST_UPDATE_DATE, w.tenant_id FROM o2md_warehouse w JOIN o2md_pos p ON p.pos_id = w.pos_id AND p.tenant_id = w.tenant_id LEFT JOIN o2md_warehouse_tl wtl ON wtl.warehouse_id = w.warehouse_id AND wtl.lang = #{lang} LEFT JOIN o2md_pos_tl ptl ON ptl.pos_id = p.pos_id AND ptl.lang = #{lang}
AND w.active_flag = 1
AND w.warehouse_code LIKE #{warehouseCodeLike}
AND wtl.warehouse_name LIKE #{warehouseNameLike}
AND p.pos_code LIKE #{posCodeLike}
AND ptl.pos_name LIKE #{posNameLike}
AND w.warehouse_status_code = #{warehouseStatusCode}
AND w.warehouse_type_code = #{warehouseTypeCode}
and w.tenant_id = #{tenantId}
and 1 = 0

SELECT w.warehouse_id, w.warehouse_code, w.warehouse_type_code, w.warehouse_status_code, w.picked_up_flag, w.pick_up_quantity, w.expressed_flag, w.expressed_quantity, w.score, w.actived_date_from, w.actived_date_to, w.inv_organization_code, p.pos_code, w.tenant_id, w.active_flag FROM o2md_warehouse w JOIN o2md_pos p ON p.pos_id = w.pos_id AND p.tenant_id = w.tenant_id and p.tenant_id = #{tenantId}
and w.tenant_id = #{tenantId}
and 1 = 0

SELECT ow.warehouse_id, ow.warehouse_code, ow.warehouse_type_code, ow.warehouse_status_code as warehouse_status FROM o2md_warehouse ow JOIN o2md_pos pos on ow.pos_id = pos.pos_id JOIN o2md_online_shop_rel_warehouse osrw ON osrw.warehouse_id = ow.warehouse_id JOIN o2md_online_shop os ON os.online_shop_id = osrw.online_shop_id WHERE ow.warehouse_status_code = 'NORMAL' AND ( pos.pos_type_code = 'WAREHOUSE' OR ( pos.pos_type_code = 'STORE' AND ow.expressed_flag = 1 ) ) AND osrw.active_flag=1 AND os.online_shop_code = #{onlineShopCode}
AND ow.tenant_id = #{tenantId} AND osrw.tenant_id = #{tenantId} AND os.tenant_id = #{tenantId}
AND 1 = 0
