mapper.select:

SELECT oft.template_id,
oft.template_code,
oft.template_name,
oft.is_free,
oft.valuation_type,
hutt.uom_type_name,
oft.valuation_uom,
hutl.uom_name,
oft.object_version_number,
oft.creation_date,
oft.created_by,
oft.last_updated_by,
oft.last_update_date
FROM o2md_freight_template oft
LEFT JOIN hpfm_uom_type hut ON oft.valuation_type=hut.uom_type_code AND hut.enabled_flag = 1
LEFT JOIN hpfm_uom_type_tl hutt ON hut.uom_type_id=hutt.uom_type_id AND hutt.lang = #{lang}
LEFT JOIN hpfm_uom hu ON oft.valuation_uom=hu.uom_code AND oft.valuation_type = hu.uom_type_code AND
hu.enabled_flag = 1
LEFT JOIN hpfm_uom_tl hutl ON hu.uom_id=hutl.uom_id AND hutl.lang = #{lang}


        AND oft.template_id LIKE #{templateId}



        AND oft.template_code LIKE #{templateCodeLike}



        AND oft.template_name LIKE #{templateNameLike}


        AND oft.is_free = #{isFree}


        AND oft.valuation_type = #{valuationTypeCode}


        AND oft.valuation_uom = #{valuationUomCode}


ORDER BY oft.template_code ASC
